import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const location = useLocation();

  return (
    <nav className="navbar">
      <div className="navbar-container">
        {/* Sección izquierda: Logo */}
        <div className="navbar-logo">
          <img src="/images/logo.png" alt="Logo" className="logo-img" />
        </div>

        {/* Sección central: Título o nombre de la página */}
        <div className="navbar-title">
          <h1>Misión PACE</h1>
        </div>

        {/* Sección derecha: Enlaces de navegación */}
        <ul className="nav-links">
          <li><Link to="/" className={location.pathname === '/' ? 'active' : ''}>Inicio</Link></li>
          <li><Link to="/acerca" className={location.pathname === '/acerca' ? 'active' : ''}>Acerca de</Link></li>
          <li><Link to="/datos" className={location.pathname === '/datos' ? 'active' : ''}>Datos</Link></li>
          <li><Link to="/recursos" className={location.pathname === '/recursos' ? 'active' : ''}>Recursos</Link></li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
