import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [scrolled, setScrolled] = useState(false);
  const location = useLocation();

  // Detectar cuando la página se desplaza o está en una subpágina
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };
    window.addEventListener('scroll', handleScroll);

    // Si no estamos en la página principal, aplicar fondo a la barra
    if (location.pathname !== '/') {
      setScrolled(true);
    } else {
      setScrolled(false);
    }
    
    return () => window.removeEventListener('scroll', handleScroll);
  }, [location]);

  return (
    <nav className={`navbar ${scrolled ? 'scrolled' : ''}`}>
      <div className="logo">Mi Logo</div>
      <ul className="nav-links">
        <li><Link to="/" className={location.pathname === '/' ? 'active' : ''}>Inicio</Link></li>
        <li><Link to="/acerca" className={location.pathname === '/acerca' ? 'active' : ''}>Acerca de</Link></li>
        <li><Link to="/datos" className={location.pathname === '/datos' ? 'active' : ''}>Datos</Link></li>
        <li><Link to="/recursos" className={location.pathname === '/recursos' ? 'active' : ''}>Recursos</Link></li>
      </ul>
    </nav>
  );
};

export default Navbar;
