import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css'; // Asegúrate de tener este archivo para estilos

const Navbar = ({ toggleTheme }) => {
  const [showResources, setShowResources] = useState(false);

  const handleMouseEnter = () => setShowResources(true);
  const handleMouseLeave = () => setShowResources(false);

  return (
    <nav className="navbar">
      <Link to="/">Inicio</Link>
      <Link to="/about">Acerca de</Link>
      <Link to="/data">Datos</Link>
      <div 
        className="resources-menu"
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
      >
        <Link to="/resources">Recursos</Link>
        {showResources && (
          <div className="submenu">
            <Link to="/resources/light-section-1">Sección I: Propiedades fundamentales de la luz</Link>
            <Link to="/resources/light-section-2">Sección II: ¿Qué determina el color que vemos?</Link>
            <Link to="/resources/light-section-3">Sección III: ¿Cómo interactúa la atmósfera con la luz?</Link>
            <Link to="/resources/light-section-4">Sección IV: ¿Cómo interactúa el océano con la luz?</Link>
            <Link to="/resources/todo-suma">PACE - ¡TODO SUMA!</Link>
            <Link to="/resources/data-resources">Recursos de datos de PACE</Link>
            <Link to="/resources/access-options">Opciones para acceder a los datos de PACE</Link>
            <Link to="/resources/maturity-levels">Acceso por nivel de madurez</Link>
            <Link to="/resources/additional-products">Productos derivados adicionales</Link>
            <Link to="/resources/fitopia">Fitopia</Link>
            <Link to="/resources/tutorials">Tutoriales y recursos</Link>
            <Link to="/resources/chlorophyll">NASA's long-term chlorophyll record</Link>
            <Link to="/resources/build-instructions">¡Construye tu propio RITMO!</Link>
            <Link to="/resources/why-pace">¿Por qué necesitamos PACE?</Link>
            <Link to="/resources/latest-news">Nuestras últimas noticias</Link>
            <Link to="/resources/maps-brochures">Mapas de historias y folletos electrónicos de PCE</Link>
            <Link to="/resources/media-gallery">Galería de medios</Link>
          </div>
        )}
      </div>
      <button onClick={toggleTheme}>Cambiar Modo</button>
    </nav>
  );
};

export default Navbar;
