import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'; 
import './Navbar.css';

function Navbar() {
  const [isDarkMode, setIsDarkMode] = useState(() => {
    const savedMode = localStorage.getItem('darkMode');
    return savedMode === 'true' ? true : false;
  });

  useEffect(() => {
    document.body.classList.toggle('dark-mode', isDarkMode);
    localStorage.setItem('darkMode', isDarkMode);
  }, [isDarkMode]);

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <div className="navbar-left">
          <img src="/path-to-logo.png" alt="Logo" className="logo-img" />
          <h1 className="navbar-title">Mi Misi√≥n PACE</h1>
        </div>
        <ul className="nav-links">
          <li><Link to="/">Inicio</Link></li>
          <li><Link to="/acerca-de">Acerca de</Link></li>
          <li>
            <Link to="/datos">Datos</Link>
            <ul className="submenu">
              <li><Link to="/datos/arrojando-luz">Arrojando Luz sobre PACE</Link></li>
              <li><Link to="/datos/acceso">Acceso a los datos de PACE</Link></li>
              <li><Link to="/datos/exploracion-fitoplancton">Exploraci√≥n del fitoplancton</Link></li>
              <li><Link to="/datos/ritmo">¬°Construye tu propio RITMO!</Link></li>
              <li><Link to="/datos/por-que-necesitamos-pace">¬øPor qu√© necesitamos PACE?</Link></li>
              <li><Link to="/datos/ultimas-noticias">Nuestras √∫ltimas noticias</Link></li>
              <li><Link to="/datos/mapas">Mapas de historias y folletos electr√≥nicos de PCE</Link></li>
              <li><Link to="/datos/galeria">Galer√≠a de medios</Link></li>
            </ul>
          </li>
          <li><Link to="/recursos">Recursos</Link></li>
        </ul>
        <button className="dark-mode-toggle" onClick={toggleDarkMode}>
          {isDarkMode ? 'üåû Modo Diurno' : 'üåô Modo Nocturno'}
        </button>
      </div>
    </nav>
  );
}

export default Navbar;
