import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Asegúrate de que esto está instalado y configurado

const Navbar = () => {
    const [isNightMode, setIsNightMode] = useState(false);
    const [openSubmenu, setOpenSubmenu] = useState(null);

    const toggleNightMode = () => {
        setIsNightMode(!isNightMode);
        document.body.classList.toggle('night-mode');
        document.querySelector('nav').classList.toggle('night-mode');
    };

    const handleMouseEnter = (menu) => {
        setOpenSubmenu(menu);
    };

    const handleMouseLeave = () => {
        setOpenSubmenu(null);
    };

    return (
        <nav>
            <button onClick={toggleNightMode}>
                {isNightMode ? 'Modo Diurno' : 'Modo Nocturno'}
            </button>
            <ul>
                <li onMouseEnter={() => handleMouseEnter('light')} onMouseLeave={handleMouseLeave}>
                    Arrojando Luz sobre PACE
                    {openSubmenu === 'light' && (
                        <ul className="submenu">
                            <li><Link to="/LightSection1">Sección I: Propiedades fundamentales de la luz</Link></li>
                            <li><Link to="/LightSection2">Sección II: ¿Qué determina el color que vemos?</Link></li>
                            <li><Link to="/LightSection3">Sección III: ¿Cómo interactúa la atmósfera con la luz?</Link></li>
                            <li><Link to="/LightSection4">Sección IV: ¿Cómo interactúa el océano con la luz?</Link></li>
                            <li><Link to="/TodoSum">PACE - ¡TODO SUMA!</Link></li>
                        </ul>
                    )}
                </li>
                <li onMouseEnter={() => handleMouseEnter('data')} onMouseLeave={handleMouseLeave}>
                    Acceso a los datos de PACE
                    {openSubmenu === 'data' && (
                        <ul className="submenu">
                            <li><Link to="/DataResources">Recursos de datos de PACE</Link></li>
                            <li><Link to="/AccessOptions">Opciones para acceder a los datos de PACE</Link></li>
                            <li><Link to="/MaturityLevels">Acceso por nivel de madurez</Link></li>
                            <li><Link to="/AdditionalProducts">Productos derivados adicionales</Link></li>
                        </ul>
                    )}
                </li>
                <li onMouseEnter={() => handleMouseEnter('phytoplankton')} onMouseLeave={handleMouseLeave}>
                    Exploración del fitoplancton
                    {openSubmenu === 'phytoplankton' && (
                        <ul className="submenu">
                            <li><Link to="/Fitopia">Fitopia</Link></li>
                            <li><Link to="/Exploration">Exploración del fitoplancton</Link></li>
                            <li><Link to="/Tutorials">Tutoriales y recursos</Link></li>
                            <li><Link to="/Chlorophyll">NASA's long-term chlorophyll record is unparalleled</Link></li>
                        </ul>
                    )}
                </li>
                <li onMouseEnter={() => handleMouseEnter('build')} onMouseLeave={handleMouseLeave}>
                    ¡Construye tu propio RITMO!
                    {openSubmenu === 'build' && (
                        <ul className="submenu">
                            <li><Link to="/BuildInstructions">Instrucciones y plantilla</Link></li>
                        </ul>
                    )}
                </li>
                <li onMouseEnter={() => handleMouseEnter('why')} onMouseLeave={handleMouseLeave}>
                    ¿Por qué necesitamos PACE?
                    {openSubmenu === 'why' && (
                        <ul className="submenu">
                            <li><Link to="/WhyPace">Ecología oceánica</Link></li>
                        </ul>
                    )}
                </li>
                <li><Link to="/LatestNews">Nuestras últimas noticias</Link></li>
                <li><Link to="/MapsBrochures">Mapas de historias y folletos electrónicos de PCE</Link></li>
                <li><Link to="/MediaGallery">Galería de medios</Link></li>
            </ul>
        </nav>
    );
};

export default Navbar;
