import React, { useState, useEffect } from 'react';
import './Navbar.css';

function Navbar() {
  const [isDarkMode, setIsDarkMode] = useState(false);

  // UseEffect para aplicar la clase dark-mode en el cuerpo al cargar la página
  useEffect(() => {
    if (isDarkMode) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
  }, [isDarkMode]);

  // Función para alternar el modo nocturno
  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <div className="navbar-left">
          <img src="images/logo.png" alt="Logo" className="logo-img" />
          <h1 className="navbar-title">Mi Misión PACE</h1>
        </div>
        <ul className="nav-links">
          <li><a href="/">Inicio</a></li>
          <li><a href="/about">Acerca de</a></li>
          <li><a href="/data">Datos</a></li>
          <li><a href="/resources">Recursos</a></li>
        </ul>
        <button className="dark-mode-toggle" onClick={toggleDarkMode}>
          {isDarkMode ? '🌞 Modo Diurno' : '🌙 Modo Nocturno'}
        </button>
      </div>
    </nav>
  );
}

export default Navbar;
