import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { FaHome, FaInfoCircle, FaDatabase, FaBook } from 'react-icons/fa'; // Íconos
import './Navbar.css';

const Navbar = () => {
  const location = useLocation();

  return (
    <nav className="navbar">
      <div className="navbar-container">
        {/* Sección izquierda: Logo y Título */}
        <div className="navbar-left">
          <img src="/images/logo.png" alt="Logo" className="logo-img" />
          <h1 className="navbar-title">Misión PACE</h1>
        </div>

        {/* Sección central: Enlaces de navegación */}
        <ul className="nav-links">
          <li>
            <Link to="/" className={location.pathname === '/' ? 'active' : ''}>
              <FaHome className="icon" /> Inicio
            </Link>
          </li>
          <li>
            <Link to="/about" className={location.pathname === 'about/acerca' ? 'active' : ''}>
              <FaInfoCircle className="icon" /> Acerca de
            </Link>
          </li>
          <li>
            <Link to="/data" className={location.pathname === '/data' ? 'active' : ''}>
              <FaDatabase className="icon" /> Datos
            </Link>
          </li>
          <li>
            <Link to="/resources" className={location.pathname === '/resources' ? 'active' : ''}>
              <FaBook className="icon" /> Recursos
            </Link>
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
