import React, { useState, useEffect } from 'react';
import './Navbar.css';

function Navbar() {
  // Cargar el modo nocturno desde localStorage o establecerlo en falso
  const [isDarkMode, setIsDarkMode] = useState(() => {
    const savedMode = localStorage.getItem('darkMode');
    return savedMode === 'true' ? true : false;
  });

  // Aplicar o eliminar la clase dark-mode en el cuerpo de la página
  useEffect(() => {
    if (isDarkMode) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }

    // Guardar el estado del modo nocturno en localStorage
    localStorage.setItem('darkMode', isDarkMode);
  }, [isDarkMode]);

  // Función para alternar entre modo diurno y nocturno
  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <div className="navbar-left">
          <img src="/path-to-logo.png" alt="Logo" className="logo-img" />
          <h1 className="navbar-title">Mi Misión PACE</h1>
        </div>
        <ul className="nav-links">
          <li><a href="/">Inicio</a></li>
          <li><a href="/acerca-de">Acerca de</a></li>
          <li><a href="/datos">Datos</a></li>
          <li><a href="/recursos">Recursos</a></li>
        </ul>
        <button className="dark-mode-toggle" onClick={toggleDarkMode}>
          {isDarkMode ? '🌞 Modo Diurno' : '🌙 Modo Nocturno'}
        </button>
      </div>
    </nav>
  );
}

export default Navbar;
